import Clipboard from "clipboard";
import Scratcher from "@global/components/activity/scratchGame/scratcher";

// const Scratcher = window.Scratcher

const ScratchGame = {
	el: "#app-scratch",
	template: `
    <div class='scratch-card sim-row'>
      <div class='scratch-card-main'>
          <div v-if="!gamefinished" class='gameStart' style="position:relative">
              <img class="displayBIG1024 img-fluid" src='https://pics.mercci22.tw/mercci22/IndexWidget/f44ace6c-5bde-489c-b79d-b7e17f73aced.jpg' />
              <img class="displaySMALL1024 img-fluid" src='https://pics.mercci22.tw/mercci22/IndexWidget/7a354b8f-17dc-44fd-8947-1dd124efb93f.jpg' />
          </div>
          <transition name="fade">
            <div v-if="gamefinished">
                <div class='finishedInfo'>
                </div>
                <div class='gameEnd'>
                  <img class="displayBIG1024 img-fluid" src='https://pics.mercci22.tw/mercci22/IndexWidget/c8bca4c5-ae65-49ec-ba6f-d72bc63f0a77.jpg' />
                  <img class="displaySMALL1024 img-fluid" src='https://pics.mercci22.tw/mercci22/IndexWidget/dee25632-8e39-49cd-89ed-a55af4ecfd33.jpg' />
                </div>
            </div>
          </transition>
          <div v-if="!gamefinished" id='scratch' style="position: absolute; left: 0; right: 0;">
              <div class="scratcher text-center" :class="{'complete': gamefinished}">
                  <img ref="scratcherImg" style="opacity:0; width: 50%; position: absolute; bottom: 100px; left: 50%; transform: translateX(-50%)" src="https://pics.mercci22.tw/mercci22/IndexWidget/6bc4a777-b9c8-4def-a3c3-60547e80aff6.png" />
                  <img ref="scratcherImgM" style="opacity:0; width: 50%; position: absolute; bottom: 100px; left: 50%; transform: translateX(-50%)" src="https://pics.mercci22.tw/mercci22/IndexWidget/fb279537-6569-47fb-82cf-382580408485.png" />
                  <canvas id='fx-scratcher' />
              </div>
          </div>
      </div>
      <div v-show="gamefinished">
          <input class="c-code" type="text" id="success_form_input" readonly="readonly" v-model="couponId"/>
          <div class='btns'>
              <a ref="copybtn" data-clipboard-action="copy" data-clipboard-target="#success_form_input" href='javascript:;' class='copy' :class="{'disabled': copied}" @click.prevent="copy">{{!copied ? '複製折扣代碼' : '複製成功'}}</a>
              <a href="/recent">官網購物去 ></a>
          </div>
      </div>
  </div>
  `,
	data: {
		gamefinished: false,
		couponId: "",
		copied: false,
		noteOpened: false,
		couponValue: 50,
		copyBtn: null,
	},

	created: function () {
		// this.getRedomValue()
		var self = this;
		if (document.createElement("canvas").getContext) {
			// 初始化遊戲
			this.$nextTick(function () {
				setTimeout(() => {
					self.initScratcher();
				}, 500);
			});
		} else {
			console.warn("裝置不支援 canvas");
		}
	},

	mounted: function () {
		this.copyBtn = new Clipboard(this.$refs.copybtn);
	},

	methods: {
		getRedomValue: function () {
			const couponValueAry = [60, 100, 80];
			const max = couponValueAry.length - 1;
			const min = 0;
			const redomNum = Math.floor(Math.random() * (max - min + 1)) + min;
			this.setState({ couponValue: couponValueAry[redomNum] });
		},

		initScratcher: function () {
			let self = this;
			let scratcher = new Scratcher("fx-scratcher");
			scratcher.addEventListener("imagesloaded", () => {
				$(".scratcher").show();
			});
			let scratcherImage;
			if (!nowEnv.NowMyEquipment) {
				// scratcherImage = 'data:image/png;base64,'
				scratcherImage =
					"data:image/png;base64,";
				var w = this.$refs.scratcherImg.width;
				var h = this.$refs.scratcherImg.height;
			} else {
				// scratcherImage = 'data:image/png;base64,'
				scratcherImage =
					"data:image/png;base64,";
				var w = this.$refs.scratcherImgM.width;
				var h = this.$refs.scratcherImgM.height;
			}

			document.getElementById("fx-scratcher").width = w;
			document.getElementById("fx-scratcher").height = h;

			scratcher.setImages(scratcherImage, w, h);
			scratcher.addEventListener("scratch", () => {
				self.scratcherProgressHandler(scratcher);
			});
		},

		scratcherProgressHandler: function (scratcher) {
			let self = this;
			let pct = (scratcher.fullAmount(32) * 100) | 0;
			// console.log(scratcher)
			if (pct < 3 && !this.gamefinished) {
				this.getCoupon();
				scratcher.removeEventListener("scratch", () => {
					self.scratcherProgressHandler(scratcher);
				});
			}
		},

		getCoupon: function () {
			const self = this;
			// self.gamefinished = true
			$.ajax({
				url: "https://event-t.mercci22.com/api/halloween2020/coupon",
				type: "PUT",
				dataType: "json",
				data: {
					Amount: self.couponValue,
				},
				success: (data) => {
					if (data.Code !== 200) {
						AlertDialog.alert(data.Message);
					} else {
						self.couponId = data.Coupon;
						self.gamefinished = true;
						// $.cookie('myCoupon', data.CouponID, {
						//   expires: 24
						// })
					}
				},
				error: function () {
					AlertDialog.alert("目前無法連接伺服器，請稍候再試！");
				},
			});
		},

		// replay: function () {
		//   this.scratcher.reset()
		//   this.getRedomValue()
		//   this.setState({
		//     gamefinished: false,
		//     copied: false,
		//     noteOpened: true
		//   })
		//   this.initScratcher()
		// },

		// close: function () {
		//   $(this.wrap.parentNode).fadeOut()
		//   // this.replay()
		// },

		// closeNote: function () {
		//   this.setState({noteOpened: false})
		// },

		copy: function () {
			this.copied = true;
			this.noteOpened = true;
		},

		// wrapRef: function (elm) {
		//   this.wrap = elm
		// }
	},
};

export default ScratchGame;
